import React from "react";
import ReactDOM from "react-dom";
import Highlight from "react-highlight-js";

import "./styles.css";

function App() {
  const content = `<table cellpadding="0" cellspacing="0" class="ng-scope" style="width: 400px; font-size: 9pt; font-family: Arial, sans-serif; line-height: normal;"><tbody><tr><td style="font-family: &quot;Arial Black&quot;, sans-serif; color: rgb(62, 62, 62); padding-bottom: 4px;"><span style="font-size: 11pt; font-family: Arial, sans-serif; font-weight: bold; color: rgb(0, 0, 0);">ომედია ადმინი</span></td></tr><tr><td style="font-family: Arial, sans-serif; color: rgb(23, 147, 206); padding-bottom: 4px;"><span style="font-size: 10pt; font-family: Arial, sans-serif; color: rgb(144, 150, 160);">Senior Developer</span></td></tr><tr><td style="font-family: Arial, sans-serif;"><a href="mailto:{email}@omedia.dev" style="text-decoration: none; color: rgb(62, 62, 62);"><span style="font-size: 9pt; font-family: Arial, sans-serif; color: rgb(0, 0, 0);">admin@example.com</span></a></td></tr><tr><td style="padding-top: 20px; padding-bottom: 8px;"><a href="https://omedia.dev" target="_blank" rel="noreferrer"><img alt="Logo" width="166" src="/assets/signature.png" style="width: 166px; height: auto; border: 0px;"></a></td></tr><tr><td style="font-family: Arial, sans-serif; color: rgb(62, 62, 62);"><span style="font-size: 9pt; font-family: Arial, sans-serif; color: rgb(95, 95, 95);">Address:</span><span style="font-size: 9pt; font-family: Arial, sans-serif; color: rgb(95, 95, 95);">90 Tsagareli St.</span></td></tr><tr><td style="font-family: Arial, sans-serif; color: rgb(62, 62, 62);"><span style="font-size: 9pt; font-family: Arial, sans-serif; color: rgb(95, 95, 95);">0194 Tbilisi, Georgia</span> </td></tr><tr><td style="font-family: Arial, sans-serif; color: rgb(62, 62, 62);"><span style="font-size: 9pt; font-family: Arial, sans-serif; color: rgb(95, 95, 95);">Office: +995 558 17 00 11</span></td></tr><tr><td style="font-family: Arial, sans-serif; color: rgb(62, 62, 62);"><span style="font-size: 9pt; font-family: Arial, sans-serif; color: rgb(95, 95, 95);">Cell: 111</span></td></tr><tr><td style="font-family: Arial, sans-serif; color: rgb(62, 62, 62); padding-top: 15px;"><a href="http://www.omedia.dev" target="_blank" rel="noopener noreferrer" style="text-decoration: none;"><span style="font-size: 9pt; text-decoration: none; font-weight: bold; font-family: Arial, sans-serif; color: rgb(0, 0, 0);">www.omedia.dev</span></a></td></tr><tr><td style="padding-top: 20px;"><span style="display: inline-block; height: 22px;"><span><a href="https://www.facebook.com/omedia.dev" target="_blank" rel="noreferrer"><img alt="Facebook icon" width="22" height="22" src="assets/sig-facebook.png" style="border: 0px; height: 22px; width: 22px;"></a>&nbsp;&nbsp;</span><span><a href="https://instagram.com/omedia.dev" target="_blank" rel="noreferrer"><img alt="Instagram icon" width="22" height="22" src="assets/sig-instagram.png" style="border: 0px; height: 22px; width: 22px;"></a>&nbsp;&nbsp;</span><span><a href="https://www.linkedin.com/company/omedia-dev" target="_blank" rel="noreferrer"><img alt="LinkedIn icon" width="22" height="22" src="assets/sig-linkedin.png" style="border: 0px; height: 22px; width: 22px;"></a></span></span></td></tr><tr><td style="font-family: Arial, sans-serif; color: rgb(155, 155, 155); padding-top: 16px; text-align: justify;"><span style="font-size: 8pt; font-family: Arial, sans-serif; color: rgb(144, 150, 160);">The content of this email is confidential and intended for the recipient specified in message only. It is strictly forbidden to share any part of this message with any third party, without a written consent of the sender. If you received this message by mistake, please reply to this message and follow with its deletion, so that we can ensure such a mistake does not occur in the future.</span></td></tr></tbody></table>`;
  return (
    <div className="App">
      <h1>Hello CodeSandbox</h1>
      <h2>Start editing to see some magic happen!</h2>
      <Highlight language="html">{content}</Highlight>
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
