// Create a react component that inputs a textarea message then performs a fetch request to localhost:3001, gets back a response as data.message and displays that message in a box below

// import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
// import Highlight from "react-highlight-js";
import 'prismjs/components/prism-python';
import './App.css';

function App() {
  const [input, setInput] = useState('');
  const [response, setResponse] = useState('');

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInput(values => ({...values, [name]: value}))
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(input);
    fetch(`http://localhost:3001?object=${JSON.stringify(input)}`, {
      method: 'GET'
    })
      .then(res => res.json())
  };


  return (
    <React.Fragment>
      <form onSubmit={handleSubmit}>
        <label>Age (Years):</label>
        <input type="number" id="age" onChange={handleChange}/><br/>

        <label>Height (CM):</label>
        <input type="number" id="height" onChange={handleChange}/><br/>

        <label>Weight (KG):</label>
        <input type="number" id="weight" onChange={handleChange}/><br/>

        <label>Activity Level (Hours):</label>
        <input type="number" id="activityLevel" onChange={handleChange}/><br/>

        <label>Gender (Male/Female):</label>
        <input type="text" id="gender" onChange={handleChange}/><br/>

        <label>Submit</label>
        <input type="submit" />
      </form>
    </React.Fragment>
  );
}

export default App;
