// Create a react component that inputs a textarea message then performs a fetch request to localhost:3001, gets back a response as data.message and displays that message in a box below

import React, { useState } from 'react';
import './App.css';

function App() {
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [response, setResponse] = useState('');

  const handleChange = (e) => {
    setMessage(e.target.value);
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/' + message, {
      method: 'GET'
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        console.log(data["Fixed_Code"]);
        setResponse(data["Fixed_Code"]); 
      })
  };


  return (
    <React.Fragment>
      <form onSubmit={handleSubmit}>
        <textarea 
          placeholder='Enter your code here'
          className='user-code'
          value={message}
          onChange={handleChange}> 
        </textarea>
        <br />
        <button 
          className='btn-submit' 
          type="submit">
            Submit
        </button>
      </form>
      <div className="answer">
        {response}
      </div>
    </React.Fragment>
  );
}

export default App;
