// Create a react component that inputs a textarea message then performs a fetch request to localhost:3001, gets back a response as data.message and displays that message in a box below

// import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
// import Highlight from "react-highlight-js";
import Editor from "@monaco-editor/react";
import ReactPrismjs from '@uiw/react-prismjs';
import 'prismjs/components/prism-python';
import './App.css';

function App() {
  const [message, setMessage] = useState('');
  // const [error, setError] = useState('');
  const [response, setResponse] = useState('');

  const handleChange = (value, event) => {
    console.log(value);
    setMessage(value);
  }

  const handleSubmit = async (e) => {
    // e.preventDefault();
    console.log(message);
    fetch('http://localhost:3001/' + message, {
      method: 'GET'
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        console.log(data["Fixed_Code"]);
        setResponse(data["Fixed_Code"]); 
      })
  };


  return (
    <React.Fragment>
      <link 
        rel="stylesheet"
        href="node_modules/highlight.js/styles/atom-one-dark.css"
      />
      <div className="parent-container">
        <div className="form-container section" >
          {/* <textarea 
              placeholder='Enter your code here...'
              value={message}
              onChange={handleChange}> 
          </textarea>   */}
          <Editor
            height="90vh"
            defaultLanguage="python"
            value={message}
            onChange={handleChange}
          />
          <br />
          <button 
            variant="info"
            className='btn btn-primary btn-submit'
            onSubmit={handleSubmit} 
            type="submit">
              Submit
          </button>
        </div>
        <br />
        <ReactPrismjs className="section" language="python" source={response} />   
      </div>
    </React.Fragment>
  );
}

export default App;
