// Create a react component that inputs a textarea message then performs a fetch request to localhost:3001, gets back a response as data.message and displays that message in a box below

import React, { useState } from 'react';
import './App.css';

function App() {
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState('');

  const handleChange = (e) => {
    setMessage(e.target.value);
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/' + message, {
      method: 'GET'
    })
      .then(res => res.json())
      .then(data => {
        console.log(data["answer"]["Fixed_Code"]);
        setResponse(data["answer"]["Fixed_Code"]); 
      })
  };


  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <textarea 
          value={message}
          onChange={handleChange}> 
        </textarea>
        <button type="submit">Submit</button>
      </form>
      <div className="answer">
        {response}
      </div>
    </div>
  );
}

export default App;
