// Create a react component that inputs a textarea message then performs a fetch request to localhost:3001, gets back a response as data.message and displays that message in a box below

// import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import './App.css';

function App() {
  const [message, setMessage] = useState('');
  // const [error, setError] = useState('');
  const [response, setResponse] = useState('');

  const handleChange = (e) => {
    setMessage(e.target.value);
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/' + message, {
      method: 'GET'
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        console.log(data["Fixed_Code"]);
        setResponse(data["Fixed_Code"]); 
      })
  };


  return (
    <React.Fragment>
      <link rel="stylesheet"
      href="//cdnjs.cloudflare.com/ajax/libs/highlight.js/11.7.0/styles/default.min.css" />
      <script src="//cdnjs.cloudflare.com/ajax/libs/highlight.js/11.7.0/highlight.min.js"></script>
      <div className="parent-container">
        <form className="form-container section" onSubmit={handleSubmit}>
          <textarea 
            placeholder='Enter your code here'
            value={message}
            onChange={handleChange}> 
          </textarea>
          <br />
          <button 
            variant="info"
            className='btn btn-primary btn-submit' 
            type="submit">
              Submit
          </button>
        </form>
        <br />
        <div className="answer section">
          <pre>
            <code className="language-python">
              {response}
            </code> 
          </pre>
        </div>
      </div>
      <script src="prism.js"></script>
    </React.Fragment>
  );
}

export default App;
