// Create a react component that inputs a textarea message then performs a fetch request to localhost:3001, gets back a response as data.message and displays that message in a box below

// import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
// import Highlight from "react-highlight-js";
import 'prismjs/components/prism-python';
import './App.css';

function App() {
  const [input, setInput] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    // e.preventDefault();
    console.log(message);
    console.log(typeof message);
    fetch('http://localhost:3001/' + message, {
      method: 'GET'
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        console.log(data["Fixed_Code"]);
        setResponse(data["Fixed_Code"]); 
      })
  };


  return (
    <React.Fragment>
      <form onSubmit={handleSubmit}>
        <label>Age (Years):</label>
        <input type="number" id="age"/><br/>

        <label>Height (CM):</label>
        <input type="number" id="height"/><br/>

        <label>Weight (KG):</label>
        <input type="number" id="weight"/><br/>

        <label>Activity Level (Hours):</label>
        <input type="number" id="activityLevel"/><br/>

        <label>Gender (Male/Female):</label>
        <input type="text" id="gender"/><br/>

        <button onclick="main()" type="button" id="submit">Submit</button>
      </form>
    </React.Fragment>
  );
}

export default App;
